import { useEffect, useState } from 'react';
import './weeklytraining.css';
import {editATraining} from './../../apiServices';


function WeeklyTraining({training, getDate, runnerInfo, setAllTrainings}){
    const [feedback, setFeedback] = useState('');

    const daysToRace = new Date(runnerInfo[0].race.dateRace).getTime() - new Date(training.date).getTime();
    const differenceDays = Math.floor(daysToRace/ (1000 * 60 *60 *24));

    useEffect(()=> {
        if (feedback){
        editATraining(feedback, training._id)
        .then((updatedTraining) => {
            setAllTrainings((allTrainings)=> allTrainings.filter((training) => {
                if(training._id === updatedTraining._id){
                    training.feedback = updatedTraining.feedback;
                }
            return training
            }))
            // setAllTrainings([...allTrainings, training])
        })}
    },[])

    function clickedFeedback(value){
        setFeedback(value);
    }

    return(
        <div className={`training ${feedback}`}>
            <div><h2>{getDate(training.date)}</h2></div>
            <div>{training.distance} km</div>
            <div className='feedback'>
                <button onClick={() => clickedFeedback('light')}>üòÉ</button>
                <button onClick={() => clickedFeedback('ok')}>üòê</button>
                <button onClick={() => clickedFeedback('hard')}>ü•µ</button>
            </div>
            <div>
                <small>Days until the race: {differenceDays}</small></div>
        </div>
    )
}

export default WeeklyTraining;
