import { useEffect, useState } from 'react';
import './weeklytraining.css';


function WeeklyTraining({training, getDate, runnerInfo}){
    const [feedback, setFeedback] = useState();
    const [allTrainings, setAllTrainings] = useEffect();

    const daysToRace = new Date(runnerInfo[0].race.dateRace).getTime() - new Date(training.date).getTime();
    const differenceDays = Math.floor(daysToRace/ (1000 * 60 *60 *24));

    useEffect(()=> {
        editATraining(feedback, training._id).then((training) => [...allTrainings, setAllTrainings(training)])
    },(feedback))


    return(
        <div className='training'>
            <div><h2>{getDate(training.date)}</h2></div>
            <div>{training.distance} km</div>
            <div className='feedback'>
                <button value={feedback} onClick={(training) => setFeedback(training.target.light)}>üòÉ</button>
                <button value={feedback} onClick={(training) => setFeedback(training.target.ok)}>üòê</button>
                <button value={feedback} onClick={(training) => setFeedback(training.target.hard)}>ü•µ</button>
            </div>
            <div>
                <small>Days until the race: {differenceDays}</small></div>
        </div>
    )
}

export default WeeklyTraining;
