import { runnerCreateTrainings } from '../../apiServices';

async function createTraining(trainingsDaysFilteredHolidays, kmsPerDay, distanceRace, longDistance) {
    if (trainingsDaysFilteredHolidays.length === 0) {
      console.log('No training days available')
    }
    const trainingDate = trainingsDaysFilteredHolidays;
    let kmToRun = Number(longDistance);
    const kmToIncrease = kmsPerDay()

    function increaseKm() {
      if (kmToRun < Number(distanceRace)) {
        kmToRun += kmToIncrease
      } else {
        kmToRun = longDistance;
      }
    }
    const copyOfTrainingsDate = [...trainingDate]

    while (trainingDate.length > 0) {
      runnerCreateTrainings(copyOfTrainingsDate.shift().toISOString().split('T')[0], kmToRun)
        .then(increaseKm())
    }
  }

  export default createTraining;
